mapper.select:
select
from t_course_scheduling cs left join t_classroom classroom on cs.classroom_id = classroom.id left join t_course course on cs.course_id = course.id left join t_teacher teacher on cs.teacher_id = teacher.id left join t_classes classes on cs.classes_id = classes.id where 1=1
and ${ew.sqlSegment}

select concat(date_format(attend_time, '%H:%i'), '-', date_format(finish_time, '%H:%i')) time from t_course_scheduling where date in
#{item}
and classroom_id = #{classroomId}
group by time order by time

select date, concat(date_format(cs.attend_time, '%H:%i'), '-', date_format(cs.finish_time, '%H:%i')) time, classroom.name classroomName, course.name courseName, course.background_color backgroundColor, teacher.name teacherName, classes.name classesName from t_course_scheduling cs left join t_classroom classroom on cs.classroom_id = classroom.id left join t_course course on cs.course_id = course.id left join t_teacher teacher on cs.teacher_id = teacher.id left join t_classes classes on cs.classes_id = classes.id where date between #{startDate} and #{endDate}
and cs.classroom_id = #{classroomId}
order by date, time

select teacher.id, teacher.name, count(cs.id) count
from t_teacher teacher
left join t_course_scheduling cs on teacher.id = cs.teacher_id
    and cs.date between #{startDate} and #{endDate}
group by teacher.id, teacher.name
order by count desc;

select classes.id,classes.name, count(cs.id) count
from t_classes classes
         left join t_course_scheduling cs on classes.id = cs.classes_id
    and cs.date between #{startDate} and #{endDate}
group by classes.id, classes.name
order by count desc;

select course.id, course.name, course.background_color color, count(cs.id) count
from t_course course
left join t_course_scheduling cs on course.id = cs.course_id
    and cs.date between #{startDate} and #{endDate}
group by course.id, course.name
order by count desc;
